mapper.insert:
	insert into member
(member_id, member_pw, member_name, member_email, 
member_phone, member_birth, member_sex, member_date, 
 member_address
)
values
(#{member_id}, #{member_pw}, #{member_name}, #{member_email}, 
#{member_phone}, #{member_birth}, #{member_sex}, now(), 
#{member_address}
);

mapper.select:
SELECT *
FROM member
WHERE member_id = #{member_id}
AND member_pw = #{member_pw}

mapper.update:
UPDATE member SET member_name = #{member_name}, 
member_email = #{member_email},member_address=#{member_address}, 
member_phone=#{member_phone},member_birth=#{member_birth}
WHERE member_id = #{member_id}

mapper.select:
SELECT *
FROM member
WHERE member_id = #{member_id}
AND member_pw = #{member_pw}
